svg.script:
  		//Use the filters (true) or use flat colors (false).
  		useFilters = true;
  		//Allow free editing of colors (true) or limit user color changes to adjacent colors (false).
  		freeEdit = false;

  		//Define the colors.
  		locationColor = "#DEDACA";
		mountainColor = "#374049";
		waterColor = "#3893E0";
		terrainColors = new Array ("#705540","#FACF00","#1E5F00","#A3D000","#D78882"); //grass #98C000
		                           // canyon   desert     forest   grass   flower 
		nearLocationColors = terrainColors.slice(0);
		nearLocationColors.push(waterColor);
		fullColors = nearLocationColors.slice(0);
		fullColors.push(mountainColor);
		freeColors = fullColors.slice(0);
		freeColors.push(locationColor);
		//Define the filters (mostly borrowed Inkscape filters) for each terrain type.
		fullColorFilters = new Array ("filter102c","filter134d","filter134f","filter134g","T4","filter134w","filter102m");

		function applyFilter(element,color) {
			//Apply a filter from the array to the element.
			if (!color)
				color = element.getAttribute("fill");
			var filterIndex = fullColors.indexOf(color);
			if (useFilters) {
				if (filterIndex > -1)
					element.setAttribute("filter","url(#" + fullColorFilters[filterIndex] + ")");
				else
					element.setAttribute("filter","");
			}
			element.setAttribute("stroke","#d2bc76");
			//element.setAttribute("stroke-width","20");
		}

		function changeFill(evt) {
			//Click to change the terrain type of a tile, with special behavior on locations.
			var element = evt.target;

			if (freeEdit) {
				//If free editing is allowed, use a random color and return.
				randomFill(element,freeColors);
				return;
			}

			element.setAttribute("stroke-width",4); //handy for debugging

			if (document.getElementsByClassName("locationClass").length == 3) {
				//All three locations exist, so populate tile with (local) terrain.
				element.setAttribute("class","");
				controlledFill(element,0,true);
			} else {
				//When short on locations, click converts to a new location.
				element.setAttribute("class","locationClass");
				setFillAndFilter(element,locationColor);
			}
			element.setAttribute("stroke-width",1);
		}	

		function controlledFill(element, limit, toggle) {
			//Color or recolor (toggle) a tile based on neighboring colors if enough neighbors are colored (limit).
			var edge = element.getAttribute("data-edge");
			var tempIds = getLocalTiles(element);
			var tempFillColors = new Array ();

			for (var i = 0; i< tempIds.length; i++) {
				if ( ((tempColor = document.getElementById(tempIds[i]).getAttribute("fill")) != null)
					&& tempColor != locationColor
					&& tempFillColors.indexOf(tempColor) < 0 )
					tempFillColors.push(tempColor);
			}
			if (!limit || tempFillColors.length >= limit) {
				if (toggle && (currentColor = element.getAttribute("fill")) && ((currentColorIndex = tempFillColors.indexOf(currentColor)) > -1))
					tempFillColors.splice(currentColorIndex,1);
				randomFill(element,tempFillColors);
			}
		}	

		function initFill() {
			//Initialize the board with locations and seed terrain, then fill in the rest.

			//If the board isn't blank (saved user output), don't initialize.
			if (document.getElementById("h:1:1").getAttribute("fill")) return;

			seedLocations();
			seedTerrain(19);
			fillTerrain(7);

			function fillTerrain(tries) {
				for (j = 0; j < tries; j++) {
					var allElements = document.getElementsByTagName("polygon");
					for (var i = 0; i < allElements.length; i++) {
						var element = allElements[i];
						if (!element.getAttribute("fill") || element.getAttribute("fill") == "null") {
							controlledFill(element,3-0.5*j);
							//console.log("found an unfilled tile on try " + j);
						}
					}
				}
			}
			//Init the viewbox.
			//shape = document.getElementsByTagName("svg")[0];
			//shape.setAttribute("viewBox", "0 0 250 550"); 
		}

		function getLocalTiles(element,center) {
			//Return verified neighbors of a tile, as an array of ids.
			var col = parseInt(element.getAttribute("data-column"));
			var row = parseInt(element.getAttribute("data-row-right"));
			var tempArray = new Array ();
			var returnArray = new Array ();
			tempArray[0] = "h:" + eval(col-1) + ":" + row;
			tempArray[1] = "h:" + eval(col-1) + ":" + eval(row+1);
			tempArray[2] = "h:" + col + ":" + eval(row+1);
			tempArray[3] = "h:" + col + ":" + eval(row-1);
			tempArray[4] = "h:" + eval(col+1) + ":" + eval(row-1);
			tempArray[5] = "h:" + eval(col+1) + ":" + row;
			if (center)
				tempArray[6] = "h:" + col + ":" + row;
			for (k = 0; k < 6; k++) {
				if (document.getElementById(tempArray[k]))
					returnArray.push(tempArray[k]);
			}
			return returnArray.slice(0);
		}

		function getRowRight(col,row) {
			//Convert to the right-row number of a row.
			//Columns are vertical from left to right, row-rights are downwards diagonally from left to right.
			//Columns and virtual rows are numbered 1 to 10.
			//(This function was simpler when they were numbered 0 to 9.)
			//Row-right 0 starts at the first hex drawn (before rotation) and runs down diagonally right.
			return row - (col - Math.floor((col - 1) * .5));
		}

		function randomFill(element,colorArray) {
			//Fill in a tile at random from a provided color list (colorArray) or from the full color list.
			if (!colorArray || colorArray.length == 0) 
				colorArray = fullColors.slice(0);
			var index = Math.floor(colorArray.length*Math.random());
			setFillAndFilter(element,colorArray[index]);
		}

		function seedLocations() {
			//Place one location each away from the edges in 3 of the 4 quadrants.
			var omitQuadrant = Math.floor(Math.random()*4);
			var j = 0;
			for (i = 0; i < 4; i++) {
				if (i != omitQuadrant) {
					if (i < 2) 
						var col = Math.floor(Math.random()*3) + 2; //+2 to keep off outside edge; *3 to keep off "inside" edge
					else
						var col = Math.floor(Math.random()*3) + 7; //+5 more to shift to second quadrant
					if (i%2 == 0)
						var row = Math.floor(Math.random()*3) + 2; //use "horizontal" row numbers for simplicity; convert below
					else
						var row = Math.floor(Math.random()*3) + 7;

					var row_right = getRowRight(col,row);
					var element = document.getElementById("h:" + col + ":" + row_right)

					//color and mark with a class
					setFillAndFilter(element,locationColor);
					element.setAttribute("class","locationClass");

					//seed some terrain here
					seedLocationTerrain(element,2*j);
					seedLocationWater(col,row);
					j += 1;
				}
			}

			function seedLocationTerrain(element,colorIndex) {
				//Add two different terrain elements placed randomly next to the location, cycling through terrain array.
				var neighborTiles = getLocalTiles(element);

				//first tile
				var index = Math.floor(neighborTiles.length*Math.random());
				setFillAndFilter(document.getElementById(neighborTiles[index]),nearLocationColors[colorIndex]);
				neighborTiles.splice(index,1);
				//second tile
				index = Math.floor(neighborTiles.length*Math.random());
				setFillAndFilter(document.getElementById(neighborTiles[index]),nearLocationColors[colorIndex + 1]);
			}

			function seedLocationWater(col,row) {
				//Locations tend to be separated by water
				if (col < 4) 
					seedCol = Math.floor(Math.random()) + 5;
				else if (col > 8)
					seedCol = Math.floor(Math.random()) + 7;
				else
					seedCol = Math.floor(Math.random()) + 6;
				if (row < 4) 
					seedRow = Math.floor(Math.random()) + 5;
				else if (row > 8)
					seedRow = Math.floor(Math.random()) + 7;
				else
					seedRow = Math.floor(Math.random()) + 6;
				switch (Math.floor(Math.random()*3)) {
					case 0:
						//use both seeds
						break;
					case 1:
						//use random seedRow
						seedRow = row + (Math.floor(Math.random()*3) - 1);
						break;
					case 2:
						//use random seedCol
						seedCol = col + (Math.floor(Math.random()*3) - 1);
						break;
					case 3:
						//use random only
						seedRow = row + (Math.floor(Math.random()*3) - 1);
						seedCol = col + (Math.floor(Math.random()*3) - 1);
						break;
					case 4:
						//do nothing
						return;
				}

				var seedRowRight = getRowRight(seedCol,seedRow);
				var candidate = document.getElementById("h:" + seedCol + ":" + seedRowRight);
				//if (!candidate) debugger;
				if (!candidate.getAttribute("fill")) {
					setFillAndFilter(candidate,waterColor);
				}
			}
		}

		function seedTerrain(seeds) {
			//Extra seeding for the terrain, on top of what was added with seedLocations().
			for (i = 0; i < seeds; i++) {
				var col = Math.floor(Math.random()*10) + 1;
				var row = Math.floor(Math.random()*10);
				var row_right = row - Math.floor(col*.5); //calculate the right-row number of a row
				if (document.getElementById("h:" + col + ":" + row_right).getAttribute("fill"))
					i = i - 1;
				else
					setFillAndFilter(document.getElementById("h:" + col + ":" + row_right),fullColors[i%fullColors.length]);
			}
		}

		function setFillAndFilter(element,color) {
			//if (!element) debugger;
			element.setAttribute("fill",color);
			applyFilter(element,color);
		}
